package com.emedinaa.marvelapp.step4import android.content.Intentimport android.graphics.drawable.Drawableimport android.os.Bundleimport android.widget.Toastimport androidx.appcompat.app.AppCompatActivityimport androidx.core.content.ContextCompatfun AppCompatActivity.getImageDrawable(resource:Int):Drawable?{    return ContextCompat.getDrawable(this,resource)}fun AppCompatActivity.showToast(message:String?){    Toast.makeText(this,message,Toast.LENGTH_SHORT).show()}fun AppCompatActivity.getColorResource(resource:Int):Int{    return ContextCompat.getColor(this,resource)}fun AppCompatActivity.goToActivity(bundle:Bundle?,activity:Class<*>){    val intent= Intent(this,activity)    bundle?.let {        intent.putExtras(it)    }    startActivity(intent)}